<?php require_once("../init/initialize_admin.php");if (!$session_admin->is_logged_in()) {    redirect_to("login.php");}$query = "SELECT partner_trading_commission.partner_code as partner_code, SUM(IF(partfin.amount<0, 0.00, partfin.amount)) AS fin_comm, SUM(parttrad.amount) AS trad_comm, SUM(parttrad.amount+partfin.amount) AS total,MONTH(partfin.date) AS month,YEAR(partfin.date) AS year FROM partner_financial_activity_commission partfin,partner_trading_commission parttrad WHERE partfin.partner_code=parttrad.partner_code GROUP by partner_code,MONTH(partfin.date),YEAR(parttrad.date)";$numrows = $db_handle->numRows($query);$rowsperpage = 20;$totalpages = ceil($numrows / $rowsperpage);// get the current page or set a defaultif (isset($_GET['pg']) && is_numeric($_GET['pg'])) {   $currentpage = (int) $_GET['pg'];} else {   $currentpage = 1;}if ($currentpage > $totalpages) { $currentpage = $totalpages; }if ($currentpage < 1) { $currentpage = 1; }$prespagelow = $currentpage * $rowsperpage - $rowsperpage + 1;$prespagehigh = $currentpage * $rowsperpage;if($prespagehigh > $numrows) { $prespagehigh = $numrows; }$offset = ($currentpage - 1) * $rowsperpage;$query .= 'LIMIT ' . $offset . ',' . $rowsperpage;$result = $db_handle->runQuery($query);$all_commissions = $db_handle->fetchAssoc($result);?><!DOCTYPE html><html lang="en">    <head>        <base target="_self">        <meta charset="UTF-8">        <meta name="viewport" content="width=device-width, initial-scale=1">        <title>Instaforex Nigeria | Admin - Rewards Report</title>        <meta name="title" content="Instaforex Nigeria | Admin - Rewards Report" />        <meta name="keywords" content="" />        <meta name="description" content="" />        <?php require_once 'layouts/head_meta.php'; ?>    </head>    <body>        <?php require_once 'layouts/header.php'; ?>        <!-- Main Body: The is the main content area of the web site,