<?phprequire_once '../../init/initialize_partner.php';if (!$session_partner->is_logged_in()) {    redirect_to("../login.php");}$partner_code = $_SESSION['partner_code'];$query = "SELECT * FROM partner_financial_activity_commission WHERE partner_code = '$partner_code' ORDER BY partner_financial_activity_commission_id DESC ";$numrows = $db_handle->numRows($query);$rowsperpage = 20;$totalpages = ceil($numrows / $rowsperpage);// get the current page or set a defaultif (isset($_GET['pg']) && is_numeric($_GET['pg'])) {   $currentpage = (int) $_GET['pg'];} else {   $currentpage = 1;}if ($currentpage > $totalpages) { $currentpage = $totalpages; }if ($currentpage < 1) { $currentpage = 1; }$prespagelow = $currentpage * $rowsperpage - $rowsperpage + 1;$prespagehigh = $currentpage * $rowsperpage;if($prespagehigh > $numrows) { $prespagehigh = $numrows; }$offset = ($currentpage - 1) * $rowsperpage;$query .= 'LIMIT ' . $offset . ',' . $rowsperpage;$result = $db_handle->runQuery($query);$fin_comm = $db_handle->fetchAssoc($result);?><!DOCTYPE html><html lang="en"><head>    <base target="_self">    <meta charset="UTF-8">    <meta name="viewport" content="width=device-width, initial-scale=1">    <title>Instaforex Nigeria | Commission History</title>    <meta name="title" content="Instaforex Nigeria | Commission History" />    <meta name="keywords" content="">    <meta name="description" content="">    <?php require_once 'layouts/head_meta.php'; ?></head><body><?php require_once 'layouts/header.php'; ?><!-- Main Body: The is the main content area of the web site, contains a side bar  --><div id="main-body" class="container-fluid">    <div class="row no-gutter">        <?php require_once 'layouts/sidebar.php'; ?>        <!-- Main Body - Content Area: This is the main content area, unique for each page  -->        <div id="main-body-content-area" class="col-md-12">            <!-- Unique Page Content Starts Here            ========